import streamlit as st
from src.main import simulate_monty_hall
import time


st.title(":zap: Monty Hall Simulation")
st.image("data:image/webp;base64,", width=300)
trial_count = st.number_input("Number of Trials", 
                min_value=100, max_value=10000,
                value=100)
col1, col2 = st.columns(2)
col1.subheader("Percentage when Switching")
col2.subheader("Percentage when Staying")
chart1 = col1.line_chart(x=None, y=None, height=300) 
chart2 = col2.line_chart(x=None, y=None, height=300)  # Placeholder for chart
win_count_switch = 0
win_count_stay = 0
for i in range(trial_count):
    switch_win_rate, stay_win_rate = simulate_monty_hall(i+1)
    chart1.add_rows ([switch_win_rate / (i + 1)])
    chart2.add_rows ([stay_win_rate / (i + 1)])
    time.sleep(0.02)
    





